import { Box } from '@mui/material';
import Header from './Header';
import {useEffect, useState} from 'react';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,ArcElement
  } from 'chart.js';

import { Bar, Doughnut, Line } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    ArcElement, Legend
  );

const options = {
    axis: 'x',
    elements: {
      bar: {
        borderWidth: 2,
      },
    },
    scales: { x: { title: { display: true, text: 'Description' }}},
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
        display: true
      },
    },
  };


const InventoryAnalysis =() => {
    const [data, setData] = useState({
        datasets: [
          {
            label: 'Dataset 1',
            data:[],
            borderColor: 'rgb(258, 99, 132)',
            backgroundColor: 'rgba(25, 190, 130)',
          },



        ],
      });

    useEffect(()=> {
       const fetchData= async()=> {
           const url = 'http://localhost:3001/dd'
           const dataSet1 = [];
           const dataSet2 = [];
           const label=[];
         await fetch(url).then((data)=> {
             console.log("Api data", data)
             const res = data.json();
             return res
         }).then((res) => {
             console.log("ressss", res)
            for (const val of res) {
                dataSet1.push(val.in_rs_lakh);
                label.push(val.sr_no);
                
            }console.log(dataSet1)

          setData({
            labels:label,
            datasets: [
              {
                label: label,
                data:dataSet1,
                borderColor: 'rgb(258, 199, 132)',
                backgroundColor: 'rgba(25, 190, 130)',
                size:4,
                padding:1,
              },
            ],
          })
         }).catch(e => {
                console.log("error", e)
            })
        }
        fetchData();          
    },[])


    return(
      <Box mt='-40' display="grid"
      gridTemplateColumns="repeat(12, 1fr)"
      gridAutoRows="440px"
      gap="10px">

  <Box
  gridColumn="span 12"
  display="flex"
  alignItems="center"
  justifyContent="center"

>
<Bar data={data} options={options}/>

</Box>

<Header
        subtitle="Doughnut Chart"/>

<Box
gridColumn="span 12"
display="flex"
alignItems="center"
justifyContent="center">

<Doughnut data={data} options={options}/>
   
  </Box>
      {/* <Box

          gridColumn="span 6"
          display="flex"
          alignItems="center"
          justifyContent="center"
        >
        <Bar data={data2} options={options2}/>
      </Box>  */}
      </Box>
       )
  }
export default InventoryAnalysis;
