import { Box } from "@mui/material";
import { DataGrid, GridToolbar } from "@mui/x-data-grid";
import { tokens } from "../../theme";
import { mockTransLine } from "../../data/mockData";
import Header from "../../components/Header";
import { useTheme } from "@mui/material";
import { ResponsiveBar } from "@nivo/bar";
import { Fragment, useEffect, useState } from "react";



const TransmissionlineDash = ({isDashboard = false }) => {
  const[tbs,settbs] = useState([]);

  const gettb=async () => {
    try {
      const response = await fetch("http://localhost:3001/tb");
      const jsonData = await response.json();

      settbs(jsonData);
    }catch(err){
      console.error(err.message);
    }
  }

  useEffect(()=>{
  gettb();
},[]);
console.log(tbs);
  const theme = useTheme();
  const colors = tokens(theme.palette.mode);

  const columns = [
    { field: "id", headerName: "Voltage Level", flex: 2.5 },
    { field: "TrippingCount", headerName: "Total CKT Km" , flex:2.5},
    {
      field: "TotalOutageT",
      headerName: "Total NoOf Lines",
      flex: 2.5,
      cellClassName: "name-column--cell",
    },
    
  ];

  return (
    <Box m="20px">
      <Header
        title="TRANSMISSION LINE DASHBOARD"
        subtitle="Transmission Line details"
      />
      <Box
        display="flex"
        m="40px 0 0 0"
        height="70vh"
        sx={{
          "& .MuiDataGrid-root": {
            border: "none",
          },
          "& .MuiDataGrid-cell": {
            borderBottom: "none",
          },
          "& .name-column--cell": {
            color: colors.greenAccent[300],
          },
          "& .MuiDataGrid-columnHeaders": {
            backgroundColor: colors.blueAccent[700],
            borderBottom: "none",
          },
          "& .MuiDataGrid-virtualScroller": {
            backgroundColor: colors.primary[400],
          },
          "& .MuiDataGrid-footerContainer": {
            borderTop: "none",
            backgroundColor: colors.blueAccent[700],
          },
          "& .MuiCheckbox-root": {
            color: `${colors.greenAccent[200]} !important`,
          },
          "& .MuiDataGrid-toolbarContainer .MuiButton-text": {
            color: `${colors.grey[100]} !important`,
          },
        }}
      >
{/*        <DataGrid
//           rows={mockTransLine}
//           columns={columns}
//           components={{ Toolbar: GridToolbar }}
//         /> */}
<table className="table" class="table mt-5 text-center " width='600vh' >
<thead justifyContent='center'>
  <tr >
    <th >Voltage Level (KV)</th>
    <th>Total Circuit KM</th>
    <th>Number of Lines</th>
  </tr>

</thead>
<tbody class="table mt-5 text-center" justifyContent='center' alignItems='center'>
  {tbs.map(tb=>(
    <tr key = {tb.voltagelevel}>
      <td>{tb.voltagelevel}</td>
      <td>{tb.ckt}</td>
      <td>{tb.nolines}</td>
    </tr>
  ))}
</tbody>
</table>
      </Box>
      <Box m="10px">
      <Header title = " TRANSMISSION LINE DASHBOARD" subtitle ="Transmission Line Details" />
      <Box height="75vh">
      <Box mt='-40' display="grid"
          gridTemplateColumns="repeat(12, 1fr)"
          gridAutoRows="440px"
          gap="10px">

      <Box
      gridColumn="span 6"
      display="flex"
      alignItems="center"
      justifyContent="center"
  >
            <ResponsiveBar
              data={tbs}
              theme={{
                // added
                axis: {
                  domain: {
                    line: {
                      stroke: colors.greenAccent[500],
                    },
                  },
                  legend: {
                    text: {
                      fill: colors.grey[100],
                    },
                  },
                  ticks: {
                    line: {
                      stroke: colors.greenAccent[500],
                      strokeWidth: 1,
                    },
                    text: {
                      fill: colors.grey[100],
                    },
                  },
                },
                legends: {
                  text: {
                    fill: colors.grey[100],
                  },
                },
              }}
              keys={["TrippingCount"]}
              indexBy="id"
              margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
              padding={0.3}
              valueScale={{ type: "linear" }}
              indexScale={{ type: "band", round: true }}
              color={isDashboard ? {datum: colors.greenAccent[500]} : { scheme: "nivo" }}
              defs={[
                {
                  id: "dots",
                  type: "patternDots",
                  background: "inherit",
                  color: "#38bcb2",
                  size: 4,
                  padding: 1,
                  stagger: true,
                },
                {
                  id: "lines",
                  type: "patternLines",
                  background: "inherit",
                  color: "#eed312",
                  rotation: -45,
                  lineWidth: 6,
                  spacing: 10,
                },
              ]}
              borderColor={{
                from: "color",
                modifiers: [["darker", "1.6"]],
              }}
              axisTop={null}
              axisRight={null}
              axisBottom={{
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: isDashboard ? undefined : "Voltage Level", // changed
                legendPosition: "middle",
                legendOffset: 32,
              }}
              axisLeft={{
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: isDashboard ? undefined : "CKT Km", // changed
                legendPosition: "middle",
                legendOffset: -50,
              }}
              enableLabel={false}
              labelSkipWidth={12}
              labelSkipHeight={12}
              labelTextColor={{
                from: "color",
                modifiers: [["darker", ]],
              }}
              legends={[
                {
                  dataFrom: "keys",
                  anchor: "bottom-right",
                  direction: "column",
                  justify: false,
                  translateX: 120,
                  translateY: 0,
                  itemsSpacing: 2,
                  itemWidth: 100,
                  itemHeight: 20,
                  itemDirection: "left-to-right",
                  itemOpacity: 0.85,
                  symbolSize: 20,
                  effects: [
                    { 
                      on: "hover",
                      style: {
                        itemOpacity: 1,
                      },
                    },
                  ],
                },
              ]}
              isInteractive={tokens('darker').greenAccent[500]}
              
            />
          </Box>
          <Box
            gridColumn="span 6"
            display="flex"
            alignItems="center"
            justifyContent="center"

  >

    <ResponsiveBar
          data={mockTransLine}
          theme={{
            // added
            axis: {
              domain: {
                line: {
                  stroke: colors.grey[200],
                },
              },
              legend: {
                text: {
                  fill: colors.grey[200],
                },
              },
              ticks: {
                line: {
                  stroke: colors.grey[200],
                  strokeWidth: 1,
                },
                text: {
                  fill: colors.grey[200],
                },
              },
            },
            legends: {
              text: {
                fill: colors.grey[100],
              },
            },
          }}
          keys={["TotalOutageT"]}
          indexBy="id"
          margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
          padding={0.3}
          valueScale={{ type: "linear" }}
          indexScale={{ type: "band", round: true }}
          colors={{ scheme: "nivo" }}
          defs={[
            {
              id: "dots",
              type: "patternDots",
              background: "inherit",
              color: "#38bcb2",
              size: 4,
              padding: 1,
              stagger: true,
            },
            {
              id: "lines",
              type: "patternLines",
              background: "inherit",
              color: "#eed312",
              rotation: -45,
              lineWidth: 6,
              spacing: 10,
            },
          ]}
          borderColor={{
            from: "color",
            modifiers: [["darker", "1.6"]],
          }}
          axisTop={null}
          axisRight={null}
          axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: isDashboard ? undefined : "Voltage Level", // changed
            legendPosition: "middle",
            legendOffset: 32,
          }}
          axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: isDashboard ? undefined : "Total no of Transmission Lines", // changed
            legendPosition: "middle",
            legendOffset: -50,
          }}
          enableLabel={false}
          labelSkipWidth={12}
          labelSkipHeight={12}
          labelTextColor={{
            from: "color",
            modifiers: [["darker", 1.6]],
          }}
          legends={[
            {
              dataFrom: "keys",
              anchor: "bottom-right",
              direction: "column",
              justify: false,
              translateX: 120,
              translateY: 0,
              itemsSpacing: 2,
              itemWidth: 100,
              itemHeight: 20,
              itemDirection: "left-to-right",
              itemOpacity: 0.85,
              symbolSize: 20,
              effects: [
                {
                  on: "hover",
                  style: {
                    itemOpacity: 1,
                  },
                },
              ],
            },
          ]}
          role="application"
          barAriaLabel={function (e) {
            return e.TotalOutageT;
          }}
        />

          </Box>
        </Box>  
      </Box>
    </Box>
    </Box>
    
  );
};

export default TransmissionlineDash;
