import { Box } from "@mui/material";
import { DataGrid, GridToolbar } from "@mui/x-data-grid";
import { tokens } from "../../theme";
import { mockTransLine } from "../../data/mockData";
import Header from "../../components/Header";
import { useTheme } from "@mui/material";
import { Fragment, useEffect, useState } from "react";


const TrippingBreakdown = () => {
  const[tbs,settbs] = useState([]);

  const gettb=async () => {
    try {
      const response = await fetch("http://localhost:3001/tb");
      const jsonData = await response.json();

      settbs(jsonData);
    }catch(err){
      console.error(err.message);
    }
  }

  useEffect(()=>{
  gettb();
},[]);
console.log(tbs);

  const theme = useTheme();
  const colors = tokens(theme.palette.mode);
  

  const columns = [
    { field: "voltagelevel", headerName: "Voltage Level", flex: 2.5 },
    { field: "ckt", headerName: "Total CKT Km" , flex:2.5},
    {
      field: "nolines",
      headerName: "Total No Of Lines",
      flex: 2.5,
      cellClassName: "name-column--cell",
    },
  ];

  const rows= tbs.map(tb=>([
    {id: 1,
      voltagelevel:tb.voltagelevel,
    },
    {id: 2, ckt: tb.ckt},
    {id: 3, nolines: tb.nolines}
  ]
  ))

  return (
    <Box m="20px">
      <Header
        title="TRIPPING BREAKDOWN"
        subtitle="Trip Breakdown Details"
      />
      {/* <Fragment>
        {" "}
        <table class="table mt-5 text-center">
          <thead>
            <tr>
              <th>Voltage Level (KV)</th>
              <th>Total Circuit KM</th>
              <th>Number of Lines</th>
            </tr>

          </thead>
          <tbody>
            {tbs.map(tb=>(
              <tr key = {tb.voltagelevel}>
                <td>{tb.voltagelevel}</td>
                <td>{tb.ckt}</td>
                <td>{tb.nolines}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </Fragment> */}
      <Box
        m="40px 0 0 0"
        height="75vh"
        sx={{
          "& .MuiDataGrid-root": {
            border: "none",
          },
          "& .MuiDataGrid-cell": {
            borderBottom: "none",
          },
          "& .name-column--cell": {
            color: colors.greenAccent[300],
          },
          "& .MuiDataGrid-columnHeaders": {
            backgroundColor: colors.blueAccent[700],
            borderBottom: "none",
          },
          "& .MuiDataGrid-virtualScroller": {
            backgroundColor: colors.primary[400],
          },
          "& .MuiDataGrid-footerContainer": {
            borderTop: "none",
            backgroundColor: colors.blueAccent[700],
          },
          "& .MuiCheckbox-root": {
            color: `${colors.greenAccent[200]} !important`,
          },
          "& .MuiDataGrid-toolbarContainer .MuiButton-text": {
            color: `${colors.grey[100]} !important`,
          },
        }}
      >
        <DataGrid
          rows={rows}
          columns={columns}
          components={{ Toolbar: GridToolbar }}
        />
      </Box>
    </Box>
    
  );
};

export default TrippingBreakdown;
